// Para el eje de tiempo en charts...

export const cuantos_meses = 13; // Cuántos meses hacia atrás vamos a graficar.
export const ultimo_dia_del_mes = parseInt(Date.today().moveToLastDayOfMonth().toString('d'));

let mi_array;

// Para los días del mes:
mi_array = [];
for (let i = 1; i <= ultimo_dia_del_mes; i++) {
    mi_array.push(i);
}
export const chartlabels_dias_del_mes = mi_array;

// Para los últimos 30 días, en reversa:
mi_array = [];
for (let i = ultimo_dia_del_mes; i >= 0; i--) {
    let ii = -Math.abs(i);
    mi_array.push(Date.today().addDays(ii).toString('d'));
}
export const chartlabels_30dias_reversa = mi_array;

// Para los últimos 12 meses en reversa, incluyendo este mes en el eje x:
mi_array = [];
for (let i = 12; i >= 0; i--) {
    let ii = -Math.abs(i),
        mes = Date.today().addMonths(ii).toString('MMM').charAt(0).toUpperCase();
    mi_array.push(mes);
}
export const chartlabels_12meses_con_este_mes = mi_array;

// Para los últimos 12 meses en reversa, sin este mes en el eje x:
mi_array = [];
for (let i = cuantos_meses; i >= 1; i--) {
    let ii = -Math.abs(i),
        mes = Date.today().addMonths(ii).toString('MMM').charAt(0).toUpperCase();
    mi_array.push(mes);
}
export const chartlabels_12meses_sin_este_mes = mi_array;

// Para los años desde 2011 hasta el año pasado:
mi_array = [];
for (let i = 2011; i <= Date.today().getFullYear() - 1; i++) {
    mi_array.push(i);
}
export const mis_years = mi_array;
